package projectone;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int i = 20;
	while(i>=1) {
		System.out.println(i);
		i--;
		}
	
	
	int k = 20;
	while(k<=50) {
		System.out.println(k);
		k++;
	}
	
	int m = 30;
	while(m>=20) {
		System.out.println(m);
		m--;
	}
	
	
	
	}

}
